INSERT INTO `HPDBSpring`.`faq` (`id`, `faq`, `faq_category_id`, `answer`) VALUES ('21', '¿Cómo agrego o cambio mi foto de perfil?', 6, 'Dirijase a: Menu (esquina superior izquierda) > Configuraciones > Perfil');
INSERT INTO `HPDBSpring`.`faq` (`id`, `faq`, `faq_category_id`, `answer`) VALUES ('22', '¿Quién puede ver mi foto de perfil?', 6, 'Cualquier usuario de Heartrevent');
INSERT INTO `HPDBSpring`.`faq` (`id`, `faq`, `faq_category_id`, `answer`) VALUES ('23', '¿Quién puede ver la información de mi perfil?', 6, 'Solo los contactos que tu hayas establecido como de "confianza", para establecer un usuario de confianza ve a: Menu (esquina superior izquierda) > Contactos y da click en el corazon a la derecha del nombre del usuario (este se pondra rojo)');
INSERT INTO `HPDBSpring`.`faq` (`id`, `faq`, `faq_category_id`, `answer`) VALUES ('24', '¿Quién puede ver mis publicaciones hechas?', 6, 'Cualquier usuario de HeartPrevent');
INSERT INTO `HPDBSpring`.`faq` (`id`, `faq`, `faq_category_id`, `answer`) VALUES ('25', '¿Cómo edito la información de mi perfil?', 6, 'Dirijase a: Menu (esquina superior izquierda) > Configuraciones > Cuenta');
